mapper.select:
select memid, memgrade from (select rownum rnum, temp.* from (select pl.plmemid, mem.* from (SELECT sum(pltotalprice) as total, plmemid FROM purchaseLists where (select EXTRACT(MONTH FROM(SELECT ADD_MONTHS(TO_DATE(sysdate,'YYYY-MM-DD'), -1) PREV_MONTH FROM DUAL)) from dual) = (select EXTRACT(MONTH FROM plDate) AS MONTHS from dual) GROUP BY plmemid) pl, members mem where
total < 200000 and pl.plmemid = mem.memId and mem.memGrade != 'Friend'
total > 200000 and total < 500000 and pl.plmemid = mem.memId and mem.memGrade != 'VIP'
total > 500000 and pl.plmemid = mem.memId and mem.memGrade != 'VVIP'
) temp) where rnum >= #{contStart} and rnum <= #{contEnd}
