mapper.select:
select * from (select rownum rnum, temp.* from (select plmemid from (SELECT sum(pltotalprice) as total, plmemid FROM purchaseLists where (select EXTRACT(MONTH FROM(SELECT ADD_MONTHS(TO_DATE(sysdate,'YYYY-MM-DD'), -1) PREV_MONTH FROM DUAL)) from dual) = (select EXTRACT(MONTH FROM plDate) AS MONTHS from dual) GROUP BY plmemid) where
total < 200000
total > 200000 and < 500000
total > 500000
) temp) where rnum >= #{contStart} and rnum <= #{contEnd}
